import './styles/home.css'
import { useState, useEffect, useRef  } from 'react';

function App() {

  let [counter, setCounter] = useState(0)
  let [newUser, setNewUser] = useState(false);
  let [name, setName] = useState('');
  let [title, setTitle] = useState('');
  let [titleCount, setTitleCount] = useState(0);
  let [nameCount, setNameCount] = useState(0);
  let [error, setError] = useState(false);
  let [errorText, setErrorText] = useState("");
  let titleRef = useRef(titleCount);

  const tooManyWords = { 
    color: "red"
  }
  const withinRange = { 
    color: "black"
  }

  useEffect(() => {
    titleRef.current = titleCount;
  }, [titleCount])

  // click "+" button
  function changeBody() {
    let mainBody = document.getElementById("body");
    let form = document.querySelector(".new-todo-form");
    setNewUser(true);
    mainBody.classList.add("change-body");
  }

  // click "-" button
  function revertBody() {
    let mainBody = document.getElementById("body");
    mainBody.classList.remove("change-body");
    setNewUser(false);
    setError(false);
    setTitleCount(0);
    setNameCount(0);
  }


  // when u submit form
  const generate = (event) => {
    event.preventDefault();
    let titleCounter = document.querySelector('.title-counter')

    // checking for length
    if (title.length > 15) {
      setError(true)
      setErrorText("Title can't be more than 15 characters");
      return;
    } else if (name.length > 10) {
      setError(true)
      setErrorText("Name can't be more than 10 characters");
      return;
    } else {
      console.log("ref: " , titleRef)
      const nameContainers = document.querySelectorAll('.name-container');
      const nameContainerArr = Array.from(nameContainers);
      const nameDiv = document.createElement("div");
      const newTitle = document.createElement("p");
      const newName = document.createElement("p");

      newTitle.innerText = title;
      newTitle.classList.add("title-text");
      newName.innerText = name;
      newName.classList.add("name-text");
      nameDiv.classList.add("name-div");
      nameDiv.append(newTitle, newName);
      setCounter(counter += 1);

      // which container to append to
      if (counter <= 5) {
        nameContainerArr[0].append(nameDiv);
      } else if (counter > 5 && counter <= 10) {
        nameContainerArr[1].append(nameDiv);
      } else if (counter > 10 && counter <= 15) {
        nameContainerArr[2].append(nameDiv);
      }
      revertBody()
    }
  }

  return (
    <>
    <div id="body">
      <button className="plus-button" onClick={() => changeBody()}>+</button>
      {newUser ? <button onClick={() => revertBody()}>-</button> : null}
      <main className="main-container">
      <div className="name-container"></div>
      <div className="name-container"></div>
      <div className="name-container"></div>
      <div className="name-container"></div>
    {newUser ?
      <form className="new-todo-form" onSubmit={(e) => generate(e)}>
        <div className="todo-section-form">
          <label>Todolist Title:</label>
          <input className="title-input" type='text' placeholder='title' onChange={(e) => setTitleCount(e.target.value)} required />
          <span className="title-counter" style={title.length > 15 ? tooManyWords : withinRange}>{title.length}/15</span>
        </div>
        <div className="name-section-form">
          <label>Name:</label>
          <input className="name-input" type='text' placeholder='name' onChange={(e) => setNameCount(e.target.value)} required />
          < span class="name-counter" style={name.length > 10 ? tooManyWords : withinRange}>{name.length}/10</span>
        </div>
        {error ? <p className="error">{errorText}</p> : null}
        <button className="submit-form">New Todo</button>
      </form>
    : null}
  </main>
  </div>
   </>
  );
}

export default App;
