import './styles/home.css'
import { useState } from 'react';

function App() {

  let [counter, setCounter] = useState(0)
  let [newUser, setNewUser] = useState(false);

  // when u submit form
  const generateName = () => {
    setCounter(counter += 1);
    const nameContainers = document.querySelectorAll('.name-container');
    let nameContainerArr = Array.from(nameContainers);
    const name = document.createElement("div");
    name.classList.add("name");

    if (counter <= 4) {
      nameContainerArr[0].append(name);
    } else if (counter > 4 && counter <= 8) {
      nameContainerArr[1].append(name);
    } else if (counter > 8 && counter <= 12) {
      nameContainerArr[2].append(name);
    }

  }



  return (
    <>
    <body></body>
    {newUser ?
      <form>
        <p>State is true</p>
      </form>
    : null}
   <h1>Hello</h1>
      <button onClick={() => setNewUser(true)}>+</button>
  <main className="main-container">
   <div className="name-container"></div>
   <div className="name-container"></div>
   <div className="name-container"></div>
   <div className="name-container"></div>
  </main>
   </>
  );
}

export default App;
