import './styles/home.css'
import { useState, useRef } from 'react';

function App() {

  let [counter, setCounter] = useState(0)
  let [newUser, setNewUser] = useState(false);
  let [name, setName] = useState('');
  let [title, setTitle] = useState('');
  let nameRef = useRef(name);


  // when u click button

  function changeBody() {
    let mainBody = document.getElementById("body");
    setNewUser(true);
    mainBody.classList.add("change-body");
  }

  function revertBody() {
    let mainBody = document.getElementById("body");
    setNewUser(false);
    mainBody.classList.remove("change-body");
  }

  // handle change
  const handleNameChange = () => {
    let nameInput = document.querySelector('.name-input');
    setName(nameRef.c)
    console.log("name -->", name)
  }

  // when u submit form
  const generateName = () => {
    setCounter(counter += 1);
    const nameContainers = document.querySelectorAll('.name-container');
    let nameContainerArr = Array.from(nameContainers);
    const name = document.createElement("div");
    name.classList.add("name");

    if (counter <= 4) {
      nameContainerArr[0].append(name);
    } else if (counter > 4 && counter <= 8) {
      nameContainerArr[1].append(name);
    } else if (counter > 8 && counter <= 12) {
      nameContainerArr[2].append(name);
    }

  }



  return (
    <>
    <div id="body">
      <button onClick={() => changeBody()}>+</button>
      {newUser ? <button onClick={() => revertBody()}>-</button> : null}
  <main className="main-container">
   <div className="name-container"></div>
   <div className="name-container"></div>
   <div className="name-container"></div>
   <div className="name-container"></div>
    {newUser ?
      <form className="new-todo-form">
        <div className="name-section-form">
          <label>Name:</label>
          <input className="name-input "type='text' placeholder='name' required onChange={() => handleNameChange()}/>
        </div>
        <div className="todo-section-form">
          <label>Todolist Title:</label>
          <input type='text' placeholder='title' required/>
        </div>
        <button className="submit-form">New Todo</button>
      </form>
    : null}
  </main>
  </div>
   </>
  );
}

export default App;
